package com.yunluo.android.arcadehub.netplay;import java.util.ArrayList;import com.yunluo.android.arcadehub.netplay.obj.DeviceInfo;public class Category {	private String mCategoryName;	private boolean isHideTitle = false;	private ArrayList<DeviceInfo> mCategoryItem = new ArrayList<DeviceInfo>();	public Category(String mCategroyName) {		mCategoryName = mCategroyName;	}		public String getmCategoryName() {		return mCategoryName;	}	public void addItem(DeviceInfo deviceInfo) {		mCategoryItem.add(deviceInfo);	}		public int getId(DeviceInfo deviceInfo) {	    return mCategoryItem.indexOf(deviceInfo);	}	public ArrayList<DeviceInfo> getList() {		return mCategoryItem;	}		public void clear() {		if(null != mCategoryItem) {			mCategoryItem.clear();		}	}		public boolean contains(Object obj) {	    if(null == mCategoryItem || null == obj) {	        return false;	    }	    return mCategoryItem.contains(obj);	}	public boolean remove(Object obj) {	    if(null == mCategoryItem || null == obj) {	        return false;	    }	    return mCategoryItem.remove(obj);	}		/**	 * @param position	 * @return	 */	public DeviceInfo getItem(int position) {	    if(null == mCategoryItem) {	        return null;	    }	    if(true == isHideTitle) {	        return mCategoryItem.get(position);	    } else {	        if (position == 0) {	            DeviceInfo info = new DeviceInfo();	            info.setDesc(mCategoryName);	            return info;	        } else {	            return mCategoryItem.get(position - 1);	        }	    }	}		/**	 * @return 	 */	public int getItemCount() {	    if(false == isHideTitle()) {	        return mCategoryItem.size() + 1;	    } else {	        return mCategoryItem.size();	    }	}    public boolean isHideTitle() {        return isHideTitle;    }    public void setHideTitle(boolean isHideTitle) {        this.isHideTitle = isHideTitle;    }	}